import React, { Component } from 'react';

class GoalForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate: '',
      goal: '',
      timescale: '',
      completionDate: ''

    }

    this.handleGoalSubmit = this.handleGoalSubmit.bind(this);
    this.handleGoalChange = this.handleGoalChange.bind(this);
  }

  handleGoalChange(event) {
    this.setState({
      goal: event.target.value
    })
  }

  handleGoalSubmit(event) {
    event.preventDefault();
    const startDate = (new Date(Date.now())).toDateString();
    const goal = this.state.goal.trim();
    const timescale = this.state.timescale.trim();
    if (!goal || !timescale) {
      return
    }
    this.props.handleGoalToSubmit({startDate: startDate, goal:goal, timescale: timescale, completionDate: ''})
    this.setState({
      startDate: '',
      goal: '',
      timescale: '',
      completionDate: ''
    })


  }
  render() {
    return(
      <div className="goalForm">
        <form onSubmit={this.handleGoalSubmit}>
          <label htmlFor="goal-description">What is your goal : </label>
          <input value={this.state.goal} onChange={this.handleGoalChange} id="goal-description" placeholder="Describe your goal..."></input>
          <label htmlFor="timescale">Timescale to complete this goal : </label>
          <input value={this.state.timescale} id="timescale" placeholder="Enter timescale here..."></input>
          <input type="submit" value="Set your Goal"></input>
        </form>
      </div>
    );
  }
}

export default GoalForm;
