import React, { Component } from 'react';

class GoalForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      goal: '',
      timescale: ''
    }

  }

  handleGoalSubmit(event) {
    event.preventDefault();
    const goal = this.state.goal.trim();
    const timescale = this.state.timescale.trim();


  }
  render() {
    return(
      <div className="goalForm">
        <form>
          <label htmlFor="goal-description">What is your goal : </label>
          <input value={this.state.goal} id="goal-description" placeholder="Decsribe your goal..."></input>
          <label htmlFor="timescale">Timescale to complete this goal : </label>
          <input value={this.state.timescale} id="timescale" placeholder="Enter timescale here..."></input>
          <input type="submit" value="Set your Goal"></input>
        </form>
      </div>
    );
  }
}

export default GoalForm;
