import React, { Component } from 'react';

class LongTermGoal extends Component {
  constructor(props) {
    super(props);

    this.handleCompletedGoal = this.handleCompletedGoal.bind(this);
  }

  handleCompletedGoal(event) {
    //onClick should first identify the element of goalList being affected then pass that information to handleCompletedGoal
    event.preventDefault();
    const goalList = this.props.goalList;
    const index = goalList.indexOf(goalList['key']['key']);
    const completedGoal = goalList[index];
    console.log(completedGoal);
    this.props.handleCompletedGoal(completedGoal);
  }
  render() {
    return (
      <tbody>
        <tr key={this.props.id}>
          <td>{this.props.id}</td>
          <td>{this.props.description}</td>
          <td>{this.props.timescale}</td>
          <td><input type="button" key={this.props.id} value="Goal Completed &#10004;" onClick={this.props.handleCompletedGoal}/></td>
        </tr>
      </tbody>
    )
  }

  }

export default LongTermGoal;
